import React, { useState } from 'react';
import ReCAPTCHA from "react-google-recaptcha";

const axios = require('axios').default;

const init_form_data = {
  email: '',
  name: '',
  recaptcha_token:'false',
};


const AddUser = (props) => {

  	const [data, setData] = useState(init_form_data);

  	const [errors, setErrors] = useState({});

	const onChangeRecaptcha = (value) => {
		setData({
		  	...data,
		  	recaptcha_token: value,
		});
	}

	return (
		<div className="AddUser">
			<br />
			<br />
			<form className="form-horizontal">
			    <div className="form-group">
			      	<label className="control-label col-sm-2" htmlFor="email">Email:</label>
			      	<div className="col-sm-10">
			        	<input type="email" className="form-control" id="email" placeholder="Enter email" name="email" required />
			        	{errors.email && (
							<span className="alert alert-danger">
								{errors.email[0]}
							</span>
						)}
			      	</div>
			    </div>
			    <div className="form-group">
			      	<label className="control-label col-sm-2" htmlFor="name">Password:</label>
			      	<div className="col-sm-10">          
			        	<input type="text" className="form-control" id="name" placeholder="Enter name" name="name" required />
			        	{errors.name && (
							<span className="alert alert-danger">
								{errors.name[0]}
							</span>
						)}
			      	</div>
			    </div>
			    <div className="form-group">        
				    <div className="col-sm-offset-2 col-sm-10">
				        <ReCAPTCHA
							sitekey="6LeH2fsUAAAAAMMIcn_LlD08MBOvlD-4aZRFvUcB"
							onChange={onChangeRecaptcha}
			            />
			            {errors.recaptcha_token && (
							<span className="alert alert-danger">
								{errors.recaptcha_token[0]}
							</span>
						)}
				    </div>
			    </div>
			    <div className="form-group">        
			      	<div className="col-sm-offset-2 col-sm-10">
			        	<button type="button" className="btn btn-default">Add user</button>
			      	</div>
			    </div>
			</form>
		</div>
	);
};

export default AddUser;
